options.css:
#notice {
    position: fixed;
    z-index: 9999999999999999;
    bottom: 0;
    width: 100%;
    font-size: 25px;
    min-height: 80px;
    padding-top: 20px;
    padding-left: 100px;
}

#notice p {
    margin-top: 10px
}

#notice button {
    background-color: #FFF;
    margin-bottom: 10px
}

#notice button:focus,#notice button:hover {
    background-color: #1878bd
}

.btn {
    font-weight: 700;
    padding: 6px 30px;
    font-size: 18px;
    line-height: 1.42857143;
    border-radius: 24px
}

.btn-primary-outline {
    color: #1878bd;
    background-color: rgba(255,255,255,0);
    border-width: 1px;
    border-style: solid;
    border-color: #d9d9d9
}

.btn-primary-outline.active,.btn-primary-outline.focus,.btn-primary-outline:active,.btn-primary-outline:focus,.btn-primary-outline:hover {
    color: #fff;
    background-color: #1878bd;
    border-color: #d9d9d9
}

options.apply_js:
//Setup utility functions from Optimizely
var utils = window.optimizely.get('utils');

//Use waitForElement promise to wait until we want to render the extension
utils.waitForElement('body').then(function(element){
  var html = extension.$html;

  //Insert the extension
  element.insertAdjacentHTML('afterbegin', html);

  var closeBtn = document.querySelector('button.close');

  //Bind event listener on close cta  
  closeBtn.addEventListener('click', function(e) {
    e.preventDefault();
  	document.querySelector('.notice').remove();

    //Send Custom Event to Optimizely to track click
    window['optimizely'] = window['optimizely'] || [];
		window.optimizely.push({
      type: "event",
      eventName: "close_notice"
    });
  });
});
