options.css:
.optimizely-modal-widget.overlay {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 9999;
  width: 100%;
  height: 100%;
  background: rgba(253, 253, 253, 0.46);
}

.optimizely-modal-widget #popup-wrapper {
  position: fixed;
  top: 175px;
  left: 50%;
  z-index: 10000;
  display: block;
  overflow: hidden;
  margin-left: -275px;
  width: 550px;
  height: 340px;
  border: 4px solid #fff;
  background-size: 100%!important;
  background-repeat: no-repeat;
  background-position: center;
}

.optimizely-modal-widget #popup {
  overflow: hidden;
  margin-top: 25px;
  padding: 15px;
  color: #fff;
  text-align: center;
  letter-spacing: 2px;
  font-size: 14px;
  font-family: gibsonregular;
}

.optimizely-modal-widget form {
  position: absolute;
  bottom: 25px;
  width: 250px;
  left: 50%;
  margin-left: -125px;
}

options.apply_js:
var utils = window.optimizely.get('utils');

utils.waitForElement('body').then(function(element){
  var html = widget.$html;
  element.insertAdjacentHTML('afterbegin', html);
});
