options.css:
.sliding_div_right{
    position: fixed;
  	top:30%;
  	right:-15%;
    width: 400px;
    background: #9b0303;
  	border: 1px solid lightgrey;
    -webkit-animation: slide 0.5s forwards;
    -moz-animation: slide 0.5s forwards;
    animation: slide 0.5s forwards;
  	z-index:99999999999;
  	color: white;
  	font-family: Trebuchet, "Trebuchet MS", Arial, Verdana, sans-serif;
}

.name{
	text-align: center;
  font-size: 1.5em;
  font-weight: bold;
  margin-left: auto;
  margin-right: auto;
  margin-bottom: 10px;
  display: block;
  max-width: 250px;

}

.product{
  display: block;
  max-width: 350px;
  margin: 0 auto;
}

.optly-content {
  margin: 0 auto;
  padding: 5%;
}

.optly-content > h1 {
	text-align: center;
  margin-bottom: 4%;
  font-size: 1.5em;
}



.optly-cta {
	background-color: white;
  color: #9b0303;
  border: none;
  cursor: pointer;
  display: block;
  font-weight: 600;
  line-height: 17px;
  padding: 20px 75px;
  text-align: center;
  text-transform: uppercase;
  letter-spacing: 2px;
  border-radius: 3px;
  transition: background-color .2s;
  margin: 4%;
}

.optly-close {
  color: white !important;
  float: right;
  font-family: Helvetica,Arial,sans-serif;
  font-size: 14px;
  font-weight: bold;
  margin-top: 3%;
  margin-right: 3%;
}

@-webkit-keyframes slide {
    100% { right: 0; }
}

@keyframes slide {
    100% { right: 0; }
}

@-webkit-keyframes slideLeft {
    100% { left: 0; }
}

@keyframes slideLeft {
    100% { left: 0; }
}

options.apply_js:
var utils = window.optimizely.get('utils');
var behavior = window.optimizely.get('behavior');
var i = 0,
    name,
    image,
    url,
    contentBlock1;

var lastViewed = behavior.query(
  {
    "version": "0.2",
    "filter": [
      {
        "field": ["tags", ""+ extension.name +""],
        "comparator": "exists"
      }
    ],
    "pick": {
      "field": ["tags", ""+ extension.name +""]
    },
    "sort":	[{
      "field": ["frequency"],
      "direction":"descending"
    }]
  }
 );

var allViewed = behavior.query(
  {
    "version": "0.2",
    "filter": [
      {
        "field": ["tags", ""+ extension.name +""],
        "comparator": "exists"
      }
    ]
  }
);

console.warn(allViewed);
console.warn(lastViewed);
console.warn(lastViewed[0]);

utils.waitForElement('body').then(function(element){
  for(i = 0; i<=allViewed.length-1; i++){
    if(allViewed[i].tags[extension.name] === lastViewed[0]){
      name = allViewed[i].tags[extension.name];
      console.log(name);
      image = allViewed[i].tags[extension.image];
      url = allViewed[i].tags[extension.location];
    }
  }
	var html = widget.$html;
  element.insertAdjacentHTML('afterbegin', html);

  contentBlock1 = "<span class=\"name\">"+ name +"</span>\n<img src=\""+image+"\" class=\"product\"/>\n<a href=\""+ url +"\" class=\"optly-cta\">"+ extension.cta +"</a>";
  document.querySelector('.optly-content').insertAdjacentHTML('afterend', contentBlock1);
  document.querySelector(".sliding_div_right").style.display = "block";
  document.querySelector('.optly-close a').addEventListener('click', function(){
    document.querySelector(".sliding_div_right").remove();
    window['optimizely'] = window['optimizely'] || [];
    window['optimizely'].push({
      type: "event",
      eventName: "close_slide"
    });
  });
});
